<template>
  <div v-if="type === `newGroup`">
    <v-dialog v-model="show" width="500" persistent>
      <v-card>
        <v-card-title class="white--text text-h5 blue">
          新增群組
        </v-card-title>

        <v-card-text>
          <v-container>
            <v-row>
              <v-col cols="12">
                <v-text-field
                  label="Group Name"
                  v-model="inputGroupName"
                  autocomplete="false"
                  :rules="inputGroupNameRules"
                  required
                ></v-text-field>
              </v-col>
            </v-row>
          </v-container>
        </v-card-text>

        <v-divider></v-divider>

        <v-card-actions>
          <v-spacer></v-spacer>
          <v-btn color="primary" text @click="tryClose">
            close
          </v-btn>
          <v-btn color="primary" text @click="createGroup">
            create
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
  </div>
  <div v-else-if="type === `contacts`">
    <v-dialog v-model="show" width="500" persistent>
      <v-card>
        <v-card-title class="text-h5 grey lighten-2">
          聯絡人
        </v-card-title>

        <v-card-text>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua.
        </v-card-text>

        <v-divider></v-divider>

        <v-card-actions>
          <v-spacer></v-spacer>
          <v-btn color="primary" text @click="tryClose">
            close
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
  </div>
  <div v-else-if="type === `setting`">
    <v-dialog v-model="show" width="500" persistent>
      <v-card>
        <v-img class="white--text blue" height="150px">
          <v-app-bar flat color="rgba(0, 0, 0, 0)">
            <v-toolbar-title class="text-h6 white--text pl-0">
              設定
            </v-toolbar-title>
            <v-spacer></v-spacer>
            <v-menu left bottom offset-y>
              <template v-slot:activator="{ on, attrs }">
                <v-btn icon v-bind="attrs" v-on="on">
                  <v-icon color="white">mdi-dots-vertical</v-icon>
                </v-btn>
              </template>

              <v-list>
                <v-list-item @click="tryLogout()">
                  <v-list-item-title>Logout</v-list-item-title>
                </v-list-item>
              </v-list>
            </v-menu>
          </v-app-bar>

          <v-card-title class="">
            <v-avatar size="60" :color="myInfo.avatarColor">
              <span class="white--text title">
