          <div>Discription</div>
        </v-card-title>
        <!-- <v-subheader inset>logout</v-subheader>
        <v-divider inset></v-divider> -->
        <v-card-text>
          暫時無個人介紹
        </v-card-text>

        <!-- <v-card-text class="">
          <v-btn class="text-xs-center white--text red">
            Logout
          </v-btn>
        </v-card-text> -->

        <v-divider></v-divider>

        <v-card-actions>
          <v-spacer></v-spacer>
          <v-btn color="primary" text @click="tryClose">
            close
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
  </div>
  <div v-else-if="type === `tryLogout`">
    <v-dialog v-model="show" width="300" persistent>
      <v-card>
        <!-- <v-card-title class="white--text text-h5 blue"> </v-card-title> -->
        <br />
        <v-card-subtitle class="text-subtitle-1">
          確定要登出嗎?
        </v-card-subtitle>

        <v-divider></v-divider>

        <v-card-actions>
          <v-spacer></v-spacer>
