          <v-btn color="primary" text @click="confirmLogout">
            確定
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
  </div>
  <div v-else-if="type === `groupInfo`">
    <v-dialog v-model="show" width="500" persistent>
      <v-card>
        <v-img class="white--text" height="200px" :src="settingImgSrc[0]">
          <v-app-bar flat color="rgba(0, 0, 0, 0)">
            <v-toolbar-title class="text-h6 white--text pl-0">
              群組資訊
            </v-toolbar-title>
            <v-spacer></v-spacer>
          </v-app-bar>

          <v-card-title class="">
            <v-avatar size="60" color="green">
              <span class="white--text title">
                {{ groupInfoGetter.avatarColor }}
              </span>
            </v-avatar>
            <p class="ml-3">
              {{ groupInfoGetter.name }}
            </p>
          </v-card-title>
        </v-img>
        <v-card-title class="text-subtitle-2">
          <div>Discription</div>
        </v-card-title>
