        </v-card-text>

        <!-- <v-card-text class="">
          <v-btn class="text-xs-center white--text red">
            Logout
          </v-btn>
        </v-card-text> -->

        <v-divider></v-divider>

        <v-card-actions>
          <v-spacer></v-spacer>
          <v-btn color="primary" text @click="tryClose">
            close
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
  </div>
</template>
<script>
import axios from "axios";
import { mapGetters } from "vuex";

export default {
  name: "BaseDialog",
  props: {
    type: {
      type: String,
      required: true,
      default: "",
    },
    show: {
      type: Boolean,
      required: true,
      default: false,
    },
    title: {
      type: String,
      required: false,
    },
    content: {
      type: String,
      required: false,
      default: "",
    },
  },
  data() {
    return {
      dialog: this.show,
      inputGroupName: "",
      inputGroupNameRules: [
        (value) => !!value || "Required.",
        (value) => (value && value.length >= 3) || "Min 3 characters",
      ],
      settingImgSrc: [
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTecJXscDX6oaQat6_u3XzTP4jTxVo3F0NwA&usqp=CAU",
        "data:image/jpeg;base64,",
      ],
    };
  },
  watch: {
    dialog(val) {
      console.log("Dialog: ", val);
    },
  },
  computed: {
    ...mapGetters(["groupInfoGetter"]),
    myInfo() {
      let data = JSON.parse(localStorage.getItem("userInfo"));
      return data;
    },
  },
  methods: {
    tryClose() {
      this.inputGroupName = "";
      this.$emit("closeDialog");
    },
    createGroup() {
      if (this.inputGroupName.trim == "" || this.inputGroupName.length < 3) {
        this.inputGroupName = "";
        return;
      }
      const token = localStorage.getItem("token");
      console.log("Creat Group:", this.inputGroupName);

      axios
        .post(
          `${process.env.VUE_APP_API}/api/create_group`,
          {
            name: this.inputGroupName,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        )
        .then((res) => {
          if (res.status === 200) {
            console.log("CLIENT create group res:", res.data);
            this.inputGroupName = "";
            this.$emit("closeDialog");
            // this.$emit("updateRoomList");
            this.$socket.emit("createGroup", res.data.newGroupInfo._id);
            let data = {
              to: res.data.newGroupInfo._id,
              message: "群組已建立",
              time: Date.parse(new Date()),
              fromSys: true,
            };
            this.$socket.emit("sendGroupMsg", data);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    },
    tryLogout() {
      let data = "tryLogout";
      this.$emit("updateDialogStatus", data);
    },
    confirmLogout() {
      this.tryClose();
      this.$store.dispatch("logoutAction");
    },
  },
};
</script>
